	  				var file = new Blob([response.data], {type: 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet'});
	  			
	                FileSaver.saveAs(file, ($scope.selectedCompany.name).replace(/ /g,'') + "-" +$scope.buyerInvoiceXlsxObj.period +".xlsx");
